import React, { Component } from 'react';
import './App.css';
import socketIOClient from 'socket.io-client';


class App extends Component{

  constructor(){
    super();
    this.state = {
      endpoint: '192.168.0.35:4001',
      color: 'white',
      mensaje: "Escribe un mensaje"
    };
  }

  send =  () => {
    const socket = socketIOClient(this.state.endpoint);
    socket.emit('change color', this.state.color);
    socket.emit('enviar mensaje',this.state.mensaje);
  }

  setColor = (color)=>{
    this.setState({color});
  }

  componentDidMount = ()=>{
    const socket = socketIOClient(this.state.endpoint);
    setInterval(this.send(), 1000);
    socket.on('change color', (col) =>{
        document.body.style.backgroundColor = col;
    })
    socket.on('enviar mensaje',(men) => {
        console.log("rendirizar mensaje ",men)
       var htmlmensaje = `<p> ${men} </p>`;
        document.getElementsByClassName("HistorialMensajes").appendChild(htmlmensaje);
    })
  }

  handleSubmit = (event)=>{
    event.preventDefault()
    let mensaje =event.target.mensaje.value
    console.log("mensaje es:",mensaje)
    this.setState({mensaje: mensaje});
  }
  render(){

    const socket = socketIOClient(this.state.endpoint);

    return(
      <div style={{ textAlign: "center" }}>

      <button onClick={() => this.send() }>Change Color</button>


      <button id="blue" onClick={() => this.setColor('blue')}>Blue</button>
      <button id="red" onClick={() => this.setColor('red')}>Red</button>

        <form onSubmit={this.handleSubmit}>
          <input type="text" name="mensaje" />
          <button type="submit">Send</button>
        </form>

        <div className="HistorialMensajes">

        </div>
    </div>
    )

  }

}



export default App;
